StructuredTextModel.Text:
// Get length of string
Length :=LEN(In);

IF Length = 0 THEN
	No_Datas := TRUE;
	Laser_Start := FALSE;
	Laser_Stop := FALSE;
	Str_To_Pos:= FALSE;
ELSE
	No_Datas := FALSE;
	
	// Check if laser start
	IF In = 'start' THEN
		Laser_Start := TRUE;
		Laser_Stop := FALSE;
	ELSIF In = 'stop' THEN
		Laser_Start := FALSE;
		Laser_Stop := TRUE;
	ELSE	
			
		Laser_Start := FALSE;
		Laser_Stop := FALSE;
		
		Pos_Sep := FIND(In, ';');
		
		Coord_X := STRING_TO_LREAL( LEFT(In, Pos_Sep-1) );
		Coord_Y := STRING_TO_LREAL( MID(In, Length-Pos_Sep, Pos_Sep+1) );
		
		Str_To_Pos := TRUE;
	END_IF;
	
END_IF;
